angular.module("filtersApp",[]).filter("integer",function(){return function(a){var b=a;return a>=1e6?b=(a/1e6).toFixed(0)+"m":a>=1e3&&(b=(a/1e3).toFixed(0)+"k"),b}}).filter("rating",function(){return function(a){var b=a.toFixed(1);return b}}).filter("published_year",function(){return function(a){var b="("+a+")";return b}}).filter("page_count",function(){return function(a){var b="-"+a+" pages";return b}}).filter("message",function(){return function(a){return a.length>55&&(a=a.slice(0,53)+"..."),a}}).filter("reverse",function(){return function(a){return a.slice().reverse()}}).filter("display_tweet",function(){return function(a){return a&&a.length>40&&(a=a.slice(0,40)+"..."),a}});