websiteApp.service("websiteService",["$http","$q","$rootScope",function(a,b){this.get_book_details=function(a){return _deferred_request("/api/v0/book?"+a)},this.authenticate=function(a){return _deferred_post_request("/api/v0/authenticate",a)},this.update_profile=function(a){return _deferred_post_request("/api/v0/profile",a)},this.get_user_details=function(a){return _deferred_request("/api/v0/user_details?"+a)},this.get_genres=function(){return _deferred_request("/api/v0/genres")},this.get_background_image=function(){return _deferred_request("/api/v0/image")},this.get_notifications=function(a){return _deferred_request("/api/v0/notifications?id="+a.id)},this.search=function(a,b,c){return _deferred_request("/api/v0/search?count="+c+"&q="+a+"&t="+b)},this.get_info_data=function(){return _deferred_request("/api/v0/info_data")},_deferred_request=function(c){var d=b.defer();return a.get(c).then(function(a){return d.resolve(a.data)}),d.promise},_deferred_post_request=function(c,d){var e=b.defer();return a.post(c,d).then(function(a){return e.resolve(a.data)}),e.promise}}]);